INSERT INTO `job` VALUES (10,'Dip/Hnd','Colombo','6month Intership','{\"blocks\":[{\"key\":\"a8fnh\",\"text\":\"Marketing Manager Job Description Template\",\"type\":\"header-two\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":42,\"style\":\"color-rgb(44,50,65)\"},{\"offset\":0,\"length\":42,\"style\":\"fontsize-34\"},{\"offset\":0,\"length\":42,\"style\":\"fontfamily-Work Sans\\\", \\\"Helvetica Neue Light\\\", \\\"Helvetica Neue\\\", Helvetica, Arial, sans-serif\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"left\"}},{\"key\":\"78mi\",\"text\":\"We are seeking an innovative marketing manager to promote our company\'s brand and services. In this role, you will be optimizing our marketing strategies, managing the marketing department\'s budget and staff, as well as preparing forecasts.\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":240,\"style\":\"color-rgb(44,50,65)\"},{\"offset\":0,\"length\":240,\"style\":\"fontsize-22\"},{\"offset\":0,\"length\":240,\"style\":\"fontfamily-Work Sans\\\", \\\"Helvetica Neue Light\\\", \\\"Helvetica Neue\\\", Helvetica, Arial, sans-serif\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"left\"}},{\"key\":\"34der\",\"text\":\"To ensure success, you will need extensive knowledge of marketing strategies and the ability to identify new business leads. An outstanding marketing manager will be someone whose expertise translates into increased brand awareness and profitability.\",\"type\":\"unstyled\",\"depth\":0,\"inlineStyleRanges\":[{\"offset\":0,\"length\":250,\"style\":\"color-rgb(44,50,65)\"},{\"offset\":0,\"length\":250,\"style\":\"fontsize-22\"},{\"offset\":0,\"length\":250,\"style\":\"fontfamily-Work Sans\\\", \\\"Helvetica Neue Light\\\", \\\"Helvetica Neue\\\", Helvetica, Arial, sans-serif\"}],\"entityRanges\":[],\"data\":{\"text-align\":\"left\"}},{\"key\":\"9abh9\",\"text\":\"Marketing Manager Responsibilities:\",\"type\":\"header-three\",\"depth\":0,\"inlineStyleRanges\